					if (ccl == null) { this.chat.chatLine.setValue(""); ccl = ""; }
					if (posX >= 0 && posX <= 9) posX = "00" + posX;
					else if (posX > 9 && posX < 100) posX = "0" + posX;
					if (posY >= 0 && posY <= 9) posY = "00" + posY;
